mapper.update:
UPDATE Notice 
SET views = views + 1 
WHERE notice_id = #{noticeId}

UPDATE Notice 
SET 
    title = #{notice.title}, 
    content = #{notice.content},
    last_modified_by = #{notice.lastModifiedBy},
    updated_at = NOW()

    , image_path = #{notice.imagePath}


    , attachment_path = #{notice.attachmentPath}
    , attachment_name = #{notice.attachmentName}

WHERE notice_id = #{noticeId}
